<fieldset class='fs-info'><legend class='history-logs'>History Logs</legend><?if(count($history)==0){?>	<br>	<span class='warning'>No history found.</span><?}else{?>	<table width=90%>		<tr class='header'>			<td width=15%>date created</td>			<td width=60%>remarks</td>			<td>send to</td>			<td>status</td>			<td>modified by</td>		</tr>		<?foreach($history as $data){?>			<tr class='list'>				<td valign=top><?=$data['date_created']?></td>				<td valign=top><?=nl2br($data['comments'])?></td> 				<td valign=top><?=(isset($usertype_arr[$data['send_to']]) ? $usertype_arr[$data['send_to']] : 'n/a')?></td>				<td valign=top><?=(!empty($data['status'])) ? $ticket_status_arr[$data['status']] : ''?></td>				<td valign=top><?=(isset($users[$data['created_by']]) ? $users[$data['created_by']] : 'n/a')?></td>			</tr>		<?}?>	</table><?}?></fieldset>