<?php Class Manage extends Auth_Controller  { 	var $data = array();	//use `Auth_Controller` if you want the page to be validated if the user is logged in or not, if you want to disable this then use `CI_Controller` instead	public  function __construct(){		parent::__construct();		$this->load->model('manage_model');		$this->load->helper('url');		$this->load->library('session');  		$this->load->helper(array('form', 'url'));  		//$this->has_permission(178);		$this->data = $this->get_privs();	}		public function callresult($cr=''){		$this->has_permission(194);		$iden = 0;				if($cr == ''){			$this->set_header_data('','Manage Callresult');			$cr= 'callresult';			$iden = 1;		}				$data['cr_with_sub']  = $this->get_cr_with_sub();		$data['callresult'] = $this->getLookup($cr,0);		$data['cr'] = $cr;		$data['iden'] = $iden;		$this->load->view('manage/callresult',$data);				if($cr == '')			$this->load->view('templates/footer.php');	}		public function save_cr($luID=0){		$this->manage_model->save_cr($luID);	}		public 	function scripts($page=1){		$pdata = $this->input->post();		if(isset($pdata['script'])){			$this->manage_model->save_script();		}		$this->set_header_data('','Manage Script');		$data['script'] = $this->get_script($page);		$this->load->view('manage/script',$data);		$this->load->view('templates/footer.php');	}		public 	function faqs(){		$pdata = $this->input->post();		if(isset($pdata['faqs'])){			$this->manage_model->save_faqs();		}		$this->set_header_data('','Manage FAQs');		$data['script'] = $this->get_faqs();		$this->load->view('manage/faqs',$data);		$this->load->view('templates/footer.php');	}		public function view_faqs(){        $dataheader['title'] =PROJECT_NAME.':FAQs';        $dataheader['sub_title'] ='FAQs';		$dataheader['no_details'] = 1;		$this->load->view('templates/header',$dataheader);		$data['faqs'] = $this->get_faqs();		$this->load->view('manage/view_faqs',$data);		$this->load->view('templates/footer.php');	}    public function fields(){        $this->set_header_data('','Field Display');        $data['displayFields'] = $this->get_displayed_fields();        $data['dbCols'] = $this->manage_model->get_active_column($data['displayFields']);        $this->load->view('manage/fields',$data);        $this->load->view('templates/footer.php');    }    public function save_fields(){        $this->manage_model->save_fields();    }	}?>